 $ServiceName = 'WinRM'$arrService = Get-Service -Name $ServiceName  while ($arrService.Status -ne 'Running'){      Start-Service $ServiceName    write-host $arrService.status    write-host 'Service starting'        $arrService.Refresh()    if ($arrService.Status -eq 'Running')    {        Write-Host 'Service is now Running'      }  }        Get-Service | Where-Object {$_.Status -eq "Running"} | Out-File -filepath "$Env:userprofile\Desktop\active_services1.txt" 
